// --- n8n entry point ---
const raw = $('Telegram Trigger').first().json.message.text || '';

// Split lines, trim, remove empty lines, !FORM header, and instruction line
const lines = raw
  .split('\n')
  .map(l => l.trim())

// Map to JSON keys
const json = {
  name: lines[1] || '',
  email: lines[2] || '',
  website: lines[3] || ''
};

// Return in n8n format
return [
  {
    json
  }
];

// Get the input array safely
const urlArray = $input.first().json.urlset?.url || [];

// Extract all loc values
const locLinks = urlArray.map(item => item.loc).filter(Boolean);

// Join into a single string using '|'
const locString = locLinks.join('\n');

// Return as a single n8n item
return [
  {
    json: {
      loc_links: locString
    }
  }
];

function extractLinks(raw) {
  const urlRegex = /\bhttps?:\/\/\S+/gi;
  const links = raw.match(urlRegex) || [];
  const uniqueLinks = [...new Set(links.map(link => link.trim()))];
  return uniqueLinks.join('\n'); // single string with \n separator
}

// --- n8n entry point ---
const rawMarkdown = $input.first().json.results[0].markdown.raw_markdown || '';
const linksString = extractLinks(rawMarkdown);

// âœ… Return valid n8n output
return [
  {
    json: {
      links: linksString
    }
  }
];

// --- n8n entry point ---
const items = $input.all();
function normalizeMarkdown(raw) {
  let text = raw
    .replace(/[*#>`~_]+/g, ' ')
    .replace(/\[([^\]]+)\]\([^)]+\)/g, '$1')
    .replace(/\bhttps?:\/\/\S+/gi, '')
    .replace(/\|/g, ', ')
    .replace(/^- /gm, 'â€¢ ')
    .replace(/\s{2,}/g, ' ')
    .replace(/\n{2,}/g, '\n')
    .trim();

  const lines = text.split('\n');
  const sections = [];
  let current = { title: 'General', content: [] };

  for (const line of lines) {
    const heading = line.match(/^(?:#+|SECTION:)\s*(.*)/i);
    if (heading) {
      if (current.content.length) sections.push(current);
      current = { title: heading[1].trim(), content: [] };
    } else {
      current.content.push(line.trim());
    }
  }
  if (current.content.length) sections.push(current);

  const structured = sections.map(s => ({
    section: s.title,
    text: s.content
      .join(' ')
      .replace(/\s+/g, ' ')
      .replace(/\b(the|a|an|this|that|these|those)\b/gi, '')
      .trim()
  }));

  const flatText = structured.map(s => `${s.section}: ${s.text}`).join('\n\n');

  return { structured, flatText };
}
// Process all items
const output = items.map(item => {
  const rawMarkdown = item?.json?.data || '';
  const result = normalizeMarkdown(rawMarkdown);
  return {
    json: {
      structured: result.structured,
      flatText: result.flatText,
      sectionCount: result.structured.length
    }
  };
});

return output;
